import React from 'react';

export default class Audio extends React.Component {
  constructor(props) {
    super(props);
    this.audioRef = React.createRef(); // Create a ref
  }
  render() {
    return(
      <audio 
        className="clip" 
        ref={this.audioRef}
        id={this.props.letterKey}
        src={this.props.url}
      />
    )
  }
}
