import React from "react";

export default class RadioButton extends React.Component {
  onTrigger = (event) => {
    console.log(event.target.id);
    this.props.parentCallback(event.target.id);
  }

  handleRadioChange = (event) => {
    const item = event.target.value;
    this.onTrigger(event);
    this.props.onRadioChange(item); // Call a prop function for onChange
  }

  render() {
    const sectionTitle = this.props.sectionTitle;
    const btnItems = this.props.btnItems;
    const btnName = this.props.btnName;
    return (
      <>
        <div>
          <span>{sectionTitle}</span>
        </div>
        <div className="btn-container">
          <div className="btn-radio">
            {btnItems.map((item) => (
              <div className="button" key={item}>
                <input
                  type="radio"
                  name={btnName}
                  value={item}
                  id={item}
                  onClick={this.onTrigger}
                  checked={this.props.selectedItemType === item}
                  onChange={this.handleRadioChange} // Combined onChange
                />
                <label htmlFor={item}>{item}</label>
              </div>
            ))}
          </div>
        </div>
      </>
    );
  }
}
